mapper.select:
select c.event_id, c.event_timestamp, c.user_id, c.event_type
from commerce_trail c
where event_id = #{eventId}

select c.event_id, c.event_timestamp, c.user_id, c.event_type
from commerce_trail c
where user_id = #{userId}

mapper.update:
update purchase
event_id=#{eventId jdbcType=VARCHAR}, event_timestamp=#{eventTimestamp jdbcType=VARCHAR}, user_id=#{userId jdbcType=VARCHAR}, event_type=#{eventType jdbcType=VARCHAR},
where event_id = #{eventId}
