mapper.select:
SELECT owner, product_offer, member_a, member_b,
relationship, faf_indicator, creation_datetime, expiry_datetime
FROM
callingcircle
WHERE owner=#{param1}
AND product_offer=#{param2}

SELECT COUNT(*)
FROM callingcircle
WHERE owner=#{param2}
AND product_offer=#{param3}
AND member_b=#{param4}
AND relationship='SPONSER_MEMBER'

SELECT owner, product_offer, member_a, member_b,
relationship, faf_indicator, creation_datetime, expiry_datetime
FROM callingcircle
WHERE owner=#{param2}
AND product_offer=#{param3}
AND relationship='SPONSER_MEMBER'

SELECT UNIQUE member_a
FROM callingcircle
WHERE
owner=#{param2}
AND product_offer=#{param3}

SELECT COUNT(*)
FROM callingcircle
WHERE owner!=#{param2}
AND product_offer=#{param3}
AND member_b=#{param4}
AND relationship='SPONSER_MEMBER'

mapper.delete:
DELETE FROM callingcircle
WHERE owner=#{param1}
AND
product_offer=#{param2}
