mapper.select:
SELECT owner, product_offer, member_a, member_b,
relationship, faf_indicator, creation_datetime, expiry_datetime
FROM
callingcircle
WHERE owner=#{owner}
AND product_offer=#{prodcatOffer}

SELECT COUNT(*)
FROM callingcircle
WHERE owner=#{owner jdbcType=VARCHAR}
AND product_offer=#{prodcatOffer jdbcType=VARCHAR}
AND member_b=#{memberB jdbcType=VARCHAR}
AND relationship='SPONSER_MEMBER'

SELECT owner, product_offer, member_a, member_b,
relationship, faf_indicator, creation_datetime, expiry_datetime
FROM callingcircle
WHERE owner=#{owner}
AND product_offer=#{prodcatOffer}
AND relationship='SPONSER_MEMBER'

SELECT UNIQUE member_a
FROM callingcircle
WHERE
owner=#{owner}
AND product_offer=#{prodcatOffer}

SELECT COUNT(*)
FROM callingcircle
WHERE owner<>#{owner}
AND product_offer=#{prodcatOffer}
AND member_b=#{memberB}
AND relationship='SPONSER_MEMBER'

mapper.delete:
DELETE FROM callingcircle
WHERE owner=#{owner}
AND
product_offer=#{prodcatOffer}
