mapper.select:
select t.ticket_usage_id, t.ticket_id, t.event_id, t.description_en, t.created, t.consumed_units
from ticket_usage t
where ticket_usage_id = #{ticketUsageId}

mapper.update:
update ticket_usage
ticket_usage_id=#{ticketUsageId jdbcType=VARCHAR}, event_id=#{eventId jdbcType=VARCHAR}, ticket_id=#{ticket_id jdbcType=VARCHAR}, description_en=#{descriptionEn jdbcType=VARCHAR}, created=#{created jdbcType=TIMESTAMP}, consumed_units=#{consumedUnits jdbcType=VARCHAR},
where ticket_usage_id = #{ticketUsageId}

